fn run_puzzle1(input: &str) -> i32 {
    let mut floor = 0;
    for char in input.chars() {
        if char == '(' {
            floor += 1;
        } else if char == ')' {
            floor -= 1;
        }
    }
    floor
}

fn run_puzzle2(input: &str) -> usize {
    let mut floor = 0;
    let mut final_position = 0;
    for (i, char) in input.chars().enumerate() {
        if floor == -1 {
            final_position = i;
            break;
        }
        if char == '(' {
            floor += 1;
        } else if char == ')' {
            floor -= 1;
        }
    }
    final_position
}
